Sub Template_OnEdit(op)
  '{{ BEGIN_EDIT
  '--Число проводок--
  op.TransCount = 8
  op.LockUI &H8
  '--Блокировки--
  op.TransList(2).LockUI &H208
  op.TransList(3).LockUI &H200
  op.TransList(4).LockUI &H200
  op.TransList(5).LockUI &H218
  op.TransList(6).LockUI &H8
  '}} END_EDIT
End Sub


Sub Template_OnRecalc(op)
  '{{ BEGIN_RECALC
  '--Корреспонденты--
  op.CopyAgents 2, False, 1, False
  op.CopyAgents 5, False, 2, False
  op.CopyAgents 5, True, 2, False
  op.CopyAgents 6, False, 2, False
  '--Разное--
  op.CopyMiscs 26, 2, 1
  op.CopyMiscs 26, 3, 1
  op.CopyMiscs 26, 4, 1
  op.CopyMiscs 26, 5, 1
  '}} END_RECALC
End Sub
